import React, { useState, useRef, useEffect } from 'react';
import './DownloadPhase.css';

const DownloadPhase = ({ results, onStartOver }) => {
  const [selectedPreview, setSelectedPreview] = useState(null);
  const [previewLoading, setPreviewLoading] = useState(false);
  const [editorConfig, setEditorConfig] = useState(null);
  const previewContainerRef = useRef(null);
  const editorInstanceRef = useRef(null);
  
  const handleDownload = (filename) => {
    window.open(`http://localhost:5000/api/download/${filename}`, '_blank');
  };
  
  const handlePreviewClick = async (result) => {
    setSelectedPreview(result);
    setPreviewLoading(true);
  };
  
  // Load OnlyOffice editor when preview is selected
  useEffect(() => {
    if (selectedPreview) {
      const loadEditor = async () => {
        try {
          // Fetch OnlyOffice configuration from backend
          const response = await fetch(`http://localhost:5000/api/onlyoffice/config/${selectedPreview.filename}`);
          const config = await response.json();
          
          setEditorConfig(config);
          setPreviewLoading(false);
          console.log('‚úÖ OnlyOffice config loaded:', config);
        } catch (error) {
          console.error('Error loading OnlyOffice config:', error);
          setPreviewLoading(false);
        }
      };
      
      loadEditor();
    }
  }, [selectedPreview]);

  // Initialize OnlyOffice editor when config is ready
  useEffect(() => {
    if (editorConfig && previewContainerRef.current && !editorInstanceRef.current) {
      // Load OnlyOffice API script
      const script = document.createElement('script');
      script.src = 'http://localhost:8080/web-apps/apps/api/documents/api.js';
      script.async = true;
      
      script.onload = () => {
        // Clear container
        previewContainerRef.current.innerHTML = '';
        
        // Create editor div
        const editorDiv = document.createElement('div');
        editorDiv.id = 'onlyoffice-editor';
        editorDiv.style.width = '100%';
        editorDiv.style.height = '100%';
        previewContainerRef.current.appendChild(editorDiv);
        
        // Initialize OnlyOffice editor
        editorInstanceRef.current = new window.DocsAPI.DocEditor('onlyoffice-editor', editorConfig);
        console.log('‚úÖ OnlyOffice editor initialized!');
      };
      
      script.onerror = () => {
        console.error('‚ùå Failed to load OnlyOffice API script');
        setPreviewLoading(false);
      };
      
      document.body.appendChild(script);
      
      return () => {
        // Cleanup: destroy editor instance
        if (editorInstanceRef.current) {
          try {
            editorInstanceRef.current.destroyEditor();
          } catch (e) {
            console.log('Editor cleanup:', e);
          }
          editorInstanceRef.current = null;
        }
        // Remove script
        const scripts = document.querySelectorAll('script[src*="onlyoffice"]');
        scripts.forEach(s => s.remove());
      };
    }
  }, [editorConfig]);


  const handleDownloadAll = () => {
    results.forEach((result, index) => {
      setTimeout(() => {
        handleDownload(result.filename);
      }, index * 500); // Stagger downloads by 500ms
    });
  };

  return (
    <div className="download-phase">
      <div className="download-layout">
        <div className="results-panel">
      <div className="success-animation">
        <div className="success-checkmark">
          <div className="check-icon">
            <span className="icon-line line-tip"></span>
            <span className="icon-line line-long"></span>
            <div className="icon-circle"></div>
            <div className="icon-fix"></div>
          </div>
        </div>
      </div>

      <div className="phase-header">
        <h2>üéâ Formatting Complete!</h2>
        <p>Your professionally formatted resumes are ready to download</p>
      </div>

      <div className="results-summary">
        <div className="summary-card">
          <div className="summary-icon">üìÑ</div>
          <div className="summary-text">
            <span className="summary-number">{results.length}</span>
            <span className="summary-label">Resume{results.length !== 1 ? 's' : ''} Formatted</span>
          </div>
        </div>
        <div className="summary-card">
          <div className="summary-icon">‚ú®</div>
          <div className="summary-text">
            <span className="summary-number">100%</span>
            <span className="summary-label">Success Rate</span>
          </div>
        </div>
      </div>

      <div className="results-container">
        <div className="results-header">
          <h3>üì• Download Your Files</h3>
          {results.length > 1 && (
            <button className="download-all-btn" onClick={handleDownloadAll}>
              ‚¨áÔ∏è Download All ({results.length})
            </button>
          )}
        </div>

        <div className="results-grid">
          {results.map((result, index) => (
            <div 
              key={index} 
              className={`result-card ${selectedPreview?.filename === result.filename ? 'active' : ''}`}
              style={{ animationDelay: `${index * 0.1}s` }}
              onClick={() => handlePreviewClick(result)}
            >
              <div className="result-icon">
                üìù
              </div>
              <div className="result-info">
                <h4>{result.name || 'Resume'}</h4>
                <p className="original-name">{result.original}</p>
                <div className="file-meta">
                  <span className="file-type">
                    DOCX
                  </span>
                  <span className="formatted-badge">‚úì Formatted</span>
                </div>
              </div>
              <button
                className="download-btn"
                onClick={() => handleDownload(result.filename)}
              >
                <span className="download-icon">‚¨áÔ∏è</span>
                <span className="download-text">Download</span>
              </button>
            </div>
          ))}
        </div>
      </div>

          <div className="action-buttons">
            <button className="btn-start-over" onClick={onStartOver}>
              üîÑ Format More Resumes
            </button>
          </div>
        </div>

        {selectedPreview && (
          <div className="preview-panel">
            <div className="preview-header">
              <h3>‚úèÔ∏è Edit Resume</h3>
              <div className="preview-header-actions">
                <span className="edit-badge">Live Editing Enabled</span>
                <button 
                  className="close-preview-btn"
                  onClick={() => {
                    setSelectedPreview(null);
                    setPreviewLoading(false);
                    setEditorConfig(null);
                    editorInstanceRef.current = null;
                  }}
                >
                  ‚úï
                </button>
              </div>
            </div>
            <div className="preview-content">
              <div className="preview-file-info">
                <h4>{selectedPreview.name || 'Resume'}</h4>
                <p className="preview-original">{selectedPreview.original}</p>
                <p className="edit-hint">üí° Edit directly in the document below. Changes auto-save!</p>
              </div>
              <div className="preview-iframe-container">
                {previewLoading && (
                  <div className="preview-loading">
                    <div className="loading-spinner"></div>
                    <p>Loading editor...</p>
                  </div>
                )}
                {/* OnlyOffice Editor Container - Full editing capabilities */}
                <div 
                  ref={previewContainerRef}
                  className="onlyoffice-editor-container"
                  style={{ 
                    display: previewLoading ? 'none' : 'block',
                    width: '100%',
                    height: '100%',
                    minHeight: '600px'
                  }}
                />
              </div>
              <div className="preview-actions">
                <button
