import React, { useState, useRef, useEffect } from 'react';
import './DownloadPhase.css';

const DownloadPhase = ({ results, onStartOver }) => {
  const [selectedPreview, setSelectedPreview] = useState(null);
  const [previewLoading, setPreviewLoading] = useState(false);
  const [editorConfig, setEditorConfig] = useState(null);
  const previewContainerRef = useRef(null);
  const editorInstanceRef = useRef(null);
  
  const handleDownload = (filename) => {
    window.open(`http://localhost:5000/api/download/${filename}`, '_blank');
  };
  
  const handlePreviewClick = async (result) => {
    setSelectedPreview(result);
    setPreviewLoading(true);
  };
  
  // Load OnlyOffice editor when preview is selected
  useEffect(() => {
    if (selectedPreview) {
      const loadEditor = async () => {
        try {
          // Fetch OnlyOffice configuration from backend
          const response = await fetch(`http://localhost:5000/api/onlyoffice/config/${selectedPreview.filename}`);
          const config = await response.json();
          
          setEditorConfig(config);
          setPreviewLoading(false);
          console.log('‚úÖ OnlyOffice config loaded:', config);
        } catch (error) {
          console.error('Error loading OnlyOffice config:', error);
          setPreviewLoading(false);
        }
      };
      
      loadEditor();
    }
  }, [selectedPreview]);

  // Initialize OnlyOffice editor when config is ready
  useEffect(() => {
    if (editorConfig && previewContainerRef.current && !editorInstanceRef.current) {
      // Load OnlyOffice API script
      const script = document.createElement('script');
      script.src = 'http://localhost:8080/web-apps/apps/api/documents/api.js';
      script.async = true;
      
      script.onload = () => {
        // Clear container
        previewContainerRef.current.innerHTML = '';
        
        // Create editor div
        const editorDiv = document.createElement('div');
        editorDiv.id = 'onlyoffice-editor';
        editorDiv.style.width = '100%';
        editorDiv.style.height = '100%';
        previewContainerRef.current.appendChild(editorDiv);
        
        // Initialize OnlyOffice editor
        editorInstanceRef.current = new window.DocsAPI.DocEditor('onlyoffice-editor', editorConfig);
        console.log('‚úÖ OnlyOffice editor initialized!');
      };
      
      script.onerror = () => {
        console.error('‚ùå Failed to load OnlyOffice API script');
        setPreviewLoading(false);
      };
      
      document.body.appendChild(script);
      
      return () => {
        // Cleanup: destroy editor instance
        if (editorInstanceRef.current) {
          try {
            editorInstanceRef.current.destroyEditor();
          } catch (e) {
            console.log('Editor cleanup:', e);
          }
          editorInstanceRef.current = null;
        }
        // Remove script
        const scripts = document.querySelectorAll('script[src*="onlyoffice"]');
        scripts.forEach(s => s.remove());
      };
    }
  }, [editorConfig]);


  const handleDownloadAll = () => {
    results.forEach((result, index) => {
      setTimeout(() => {
        handleDownload(result.filename);
      }, index * 500); // Stagger downloads by 500ms
    });
  };

  return (
    <div className="download-phase-v2">
      {/* Ultra-Compact Status Bar */}
      <div className="status-bar">
        <div className="status-left">
          <div className="status-badge success">
            <span className="badge-icon">‚úì</span>
            <span className="badge-text">Complete</span>
          </div>
          <div className="status-info">
            <span className="info-count">{results.length}</span>
            <span className="info-label">resume{results.length !== 1 ? 's' : ''} formatted</span>
          </div>
        </div>
        <div className="status-actions">
          {results.length > 1 && (
            <button className="status-btn" onClick={handleDownloadAll} title="Download All">
              <span className="btn-icon">‚¨áÔ∏è</span>
              <span className="btn-label">All</span>
            </button>
          )}
          <button className="status-btn secondary" onClick={onStartOver} title="Format More">
            <span className="btn-icon">üîÑ</span>
            <span className="btn-label">New</span>
          </button>
        </div>
      </div>

      {/* Horizontal Tab Bar for File Selection */}
      <div className="file-tabs-bar">
        <div className="tabs-container">
          {results.map((result, index) => (
            <div
              key={index}
              className={`file-tab ${selectedPreview?.filename === result.filename ? 'active' : ''}`}
              onClick={() => handlePreviewClick(result)}
            >
              <div className="tab-icon">üìÑ</div>
              <div className="tab-content">
                <div className="tab-name">{result.name || `Resume ${index + 1}`}</div>
                <div className="tab-badge">DOCX</div>
              </div>
              <div className="tab-actions">
                <button
                  className="tab-download"
                  onClick={(e) => {
                    e.stopPropagation();
                    handleDownload(result.filename);
                  }}
                  title="Download"
                >
                  ‚¨áÔ∏è
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Full-Screen Editor */}
      <div className="editor-workspace-v2">
        {!selectedPreview ? (
          <div className="welcome-screen">
            <div className="welcome-content">
              <div className="welcome-icon">üéâ</div>
              <h1 className="welcome-title">Your Resumes Are Ready!</h1>
              <p className="welcome-subtitle">Select a resume from the tabs above to start editing</p>
              
              <div className="welcome-features">
                <div className="feature-card">
                  <div className="feature-card-icon">‚úèÔ∏è</div>
                  <h3>Full Editing Power</h3>
                  <p>Change fonts, colors, and formatting</p>
                </div>
                <div className="feature-card">
                  <div className="feature-card-icon">üíæ</div>
                  <h3>Auto-Save</h3>
                  <p>Your changes save automatically</p>
                </div>
                <div className="feature-card">
                  <div className="feature-card-icon">‚¨áÔ∏è</div>
                  <h3>Quick Download</h3>
                  <p>Download anytime with one click</p>
                </div>
              </div>

              <div className="welcome-action">
                <button 
                  className="welcome-btn"
                  onClick={() => handlePreviewClick(results[0])}
                >
                  <span>üöÄ</span> Start Editing First Resume
                </button>
              </div>
            </div>
          </div>
        ) : (
          <div className="editor-view">
            {/* Floating Editor Controls */}
            <div className="floating-controls">
              <div className="control-group">
                <span className="control-label">
                  <span className="label-icon">‚úèÔ∏è</span>
                  {selectedPreview.name || 'Resume'}
                </span>
                <span className="live-indicator">‚óè Live</span>
              </div>
              <div className="control-actions">
                <button 
                  className="control-btn primary"
                  onClick={() => handleDownload(selectedPreview.filename)}
                  title="Download"
                >
                  <span>‚¨áÔ∏è</span> Download
                </button>
                <button 
                  className="control-btn"
                  onClick={() => {
                    setSelectedPreview(null);
                    setPreviewLoading(false);
                    setEditorConfig(null);
                    editorInstanceRef.current = null;
                  }}
                  title="Close"
                >
                  ‚úï
                </button>
              </div>
            </div>

            {/* Editor Container */}
            <div className="editor-frame">
              {previewLoading && (
                <div className="editor-loader">
                  <div className="loader-spinner"></div>
                  <p className="loader-text">Loading editor...</p>
                </div>
              )}
              <div 
                ref={previewContainerRef}
                className="onlyoffice-editor-container"
                style={{ 
                  display: previewLoading ? 'none' : 'block',
                  width: '100%',
                  height: '100%'
                }}
              />
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default DownloadPhase;
