import React, { useState, useRef, useEffect } from 'react';
import './DownloadPhase.css';

const DownloadPhase = ({ results, onStartOver }) => {
  const [selectedPreview, setSelectedPreview] = useState(null);
  const [previewLoading, setPreviewLoading] = useState(false);
  const [editorConfig, setEditorConfig] = useState(null);
  const previewContainerRef = useRef(null);
  const editorInstanceRef = useRef(null);
  
  const handleDownload = (filename) => {
    window.open(`http://localhost:5000/api/download/${filename}`, '_blank');
  };
  
  const handlePreviewClick = async (result) => {
    setSelectedPreview(result);
    setPreviewLoading(true);
  };
  
  // Load OnlyOffice editor when preview is selected
  useEffect(() => {
    if (selectedPreview) {
      const loadEditor = async () => {
        try {
          // Fetch OnlyOffice configuration from backend
          const response = await fetch(`http://localhost:5000/api/onlyoffice/config/${selectedPreview.filename}`);
          const config = await response.json();
          
          setEditorConfig(config);
          setPreviewLoading(false);
          console.log('‚úÖ OnlyOffice config loaded:', config);
        } catch (error) {
          console.error('Error loading OnlyOffice config:', error);
          setPreviewLoading(false);
        }
      };
      
      loadEditor();
    }
  }, [selectedPreview]);

  // Initialize OnlyOffice editor when config is ready
  useEffect(() => {
    if (editorConfig && previewContainerRef.current && !editorInstanceRef.current) {
      // Load OnlyOffice API script
      const script = document.createElement('script');
      script.src = 'http://localhost:8080/web-apps/apps/api/documents/api.js';
      script.async = true;
      
      script.onload = () => {
        // Clear container
        previewContainerRef.current.innerHTML = '';
        
        // Create editor div
        const editorDiv = document.createElement('div');
        editorDiv.id = 'onlyoffice-editor';
        editorDiv.style.width = '100%';
        editorDiv.style.height = '100%';
        previewContainerRef.current.appendChild(editorDiv);
        
        // Initialize OnlyOffice editor
        editorInstanceRef.current = new window.DocsAPI.DocEditor('onlyoffice-editor', editorConfig);
        console.log('‚úÖ OnlyOffice editor initialized!');
      };
      
      script.onerror = () => {
        console.error('‚ùå Failed to load OnlyOffice API script');
        setPreviewLoading(false);
      };
      
      document.body.appendChild(script);
      
      return () => {
        // Cleanup: destroy editor instance
        if (editorInstanceRef.current) {
          try {
            editorInstanceRef.current.destroyEditor();
          } catch (e) {
            console.log('Editor cleanup:', e);
          }
          editorInstanceRef.current = null;
        }
        // Remove script
        const scripts = document.querySelectorAll('script[src*="onlyoffice"]');
        scripts.forEach(s => s.remove());
      };
    }
  }, [editorConfig]);


  const handleDownloadAll = () => {
    results.forEach((result, index) => {
      setTimeout(() => {
        handleDownload(result.filename);
      }, index * 500); // Stagger downloads by 500ms
    });
  };

  return (
    <div className="download-phase">
      {/* Compact Top Bar */}
      <div className="compact-top-bar">
        <div className="top-bar-left">
          <div className="success-indicator">
            <span className="success-icon">‚úì</span>
            <div className="success-text">
              <span className="success-title">Formatting Complete!</span>
              <span className="success-subtitle">{results.length} resume{results.length !== 1 ? 's' : ''} ready</span>
            </div>
          </div>
        </div>
        <div className="top-bar-actions">
          {results.length > 1 && (
            <button className="compact-btn download-all-btn" onClick={handleDownloadAll}>
              <span>‚¨áÔ∏è</span> Download All
            </button>
          )}
          <button className="compact-btn start-over-btn" onClick={onStartOver}>
            <span>üîÑ</span> Format More
          </button>
        </div>
      </div>

      {/* Main Content: Sidebar + Editor */}
      <div className="main-content-layout">
        {/* Compact Sidebar */}
        <div className="compact-sidebar">
          <div className="sidebar-header">
            <h3>üì• Your Files</h3>
          </div>
          <div className="files-list">
            {results.map((result, index) => (
              <div 
                key={index} 
                className={`file-item ${selectedPreview?.filename === result.filename ? 'active' : ''}`}
                onClick={() => handlePreviewClick(result)}
              >
                <div className="file-item-icon">üìù</div>
                <div className="file-item-info">
                  <div className="file-item-name">{result.name || 'Resume'}</div>
                  <div className="file-item-meta">
                    <span className="file-badge">DOCX</span>
                    <span className="file-status">‚úì</span>
                  </div>
                </div>
                <button
                  className="file-download-btn"
                  onClick={(e) => {
                    e.stopPropagation();
                    handleDownload(result.filename);
                  }}
                  title="Download"
                >
                  ‚¨áÔ∏è
                </button>
              </div>
            ))}
          </div>
        </div>

        {/* Large Editor Area */}
        <div className="editor-area">
          {!selectedPreview ? (
            <div className="editor-placeholder">
              <div className="placeholder-content">
                <div className="placeholder-icon">üìù</div>
                <h2>Select a Resume to Edit</h2>
                <p>Click on any file from the sidebar to start editing with full Word-like capabilities</p>
                <div className="placeholder-features">
                  <div className="feature-item">
                    <span className="feature-icon">‚úèÔ∏è</span>
                    <span>Change fonts & colors</span>
                  </div>
                  <div className="feature-item">
                    <span className="feature-icon">üé®</span>
                    <span>Format text styles</span>
                  </div>
                  <div className="feature-item">
                    <span className="feature-icon">üíæ</span>
                    <span>Auto-save changes</span>
                  </div>
                </div>
              </div>
            </div>
          ) : (
            <div className="editor-container">
              {/* Minimal Editor Header */}
              <div className="editor-header">
                <div className="editor-title">
                  <span className="edit-icon">‚úèÔ∏è</span>
                  <span className="editing-file">{selectedPreview.name || 'Resume'}</span>
                  <span className="live-badge">‚óè Live</span>
                </div>
                <div className="editor-actions">
                  <button 
                    className="editor-action-btn download-btn-header"
                    onClick={() => handleDownload(selectedPreview.filename)}
                  >
                    <span>‚¨áÔ∏è</span> Download
                  </button>
                  <button 
                    className="editor-action-btn close-btn"
                    onClick={() => {
                      setSelectedPreview(null);
                      setPreviewLoading(false);
                      setEditorConfig(null);
                      editorInstanceRef.current = null;
                    }}
                  >
                    ‚úï
                  </button>
                </div>
              </div>

              {/* Full-Size Editor */}
              <div className="editor-workspace">
                {previewLoading && (
                  <div className="editor-loading">
                    <div className="loading-spinner"></div>
                    <p>Loading editor...</p>
                  </div>
                )}
                <div 
                  ref={previewContainerRef}
                  className="onlyoffice-editor-container"
                  style={{ 
                    display: previewLoading ? 'none' : 'block',
                    width: '100%',
                    height: '100%'
                  }}
                />
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default DownloadPhase;
